#' Generates a plot relating modules eigenvalue along pseudotime
#'
#' @param metadata metadata slot generated by Omix
#' `multiomics_modules`function
#' @param modules character vector with module names to plot
#' @param variable Continuous clinical covariate
#' @param pseudotime_var Pseudotime covariate
#'
#' @return list of of plots - one for each module provided
#'
#' @family Pseudotime inference
#' @import ggplot2
#' @export
#'

pseudotime_ordering_modules <- function(metadata,
                                        modules,
                                        variable,
                                        pseudotime_var = "pseudotime") {
  range01 <- function(x) {
    (x - min(x)) / (max(x) - min(x))
  }

  pseudotime_ordering_modules <- list()

  for (i in modules) {
    metadata[, i] <- range01(metadata[, i])
    plot <- ggplot2::ggplot(metadata, ggplot2::aes_string(pseudotime_var, paste(i))) +
      ggplot2::geom_point(ggplot2::aes_string(colour = variable)) +
      ggplot2::geom_smooth(se = F) +
      ggplot2::ylim(0, 1) +
      ggplot2::theme_classic() +
      ggplot2::ylab(paste("Scaled changes (EigenValue)", i)) +
      viridis::scale_color_viridis()

    pseudotime_ordering_modules[[i]] <- plot
  }

  names(pseudotime_ordering_modules) <- modules

  return(pseudotime_ordering_modules)
}
